sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var abc = new GlideAjax('Country');
    abc.addParam('sysparm_name', 'city');
    abc.addParam('sysparm_id', newValue);
    abc.getXML(myfunc);

    function myfunc(value) {
        var ans = value.responseXML.documentElement.getAttribute('answer');
        var arr = ans.split('@');
        g_form.setValue('u_location', arr[1] + ',' + arr[0]);
		g_form.showFieldMsg('u_location','Value set automatically-you may change override by editing it ');
    }

    //Type appropriate comment here, and begin script below

}
