sys_script_include.script:
var Country = Class.create();
Country.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {                                                                                                   city : function(){
	var a = this.getParameter('sysparm_id');                                                                                                                        var xyz = new GlideRecord('sys_user');                                                                                                                        xyz.addQuery('sys_id',a);                                                                                                                                      xyz.query();                                                                                                                                                  while(xyz.next()){
		var city = xyz.location.city;
		var country = xyz.location.country;
	}
	return city+"@"+country;
},

    type: 'Country'
});
